import React from "react";
import 'bootstrap/dist/css/bootstrap.css';
import { Container, Card, Row } from 'react-bootstrap';
import "./Main.css";

const axios = require('axios');
const cheerio = require('cheerio');

async function extractImageUrl(url) {
  try {
    // Fetch the HTML content using axios
    const { data } = await axios.get(url);

    // Load the HTML content into cheerio
    const $ = cheerio.load(data);

    // Extract the image URL from meta tags (og:image)
    const ogImage = $('meta[property="og:image"]').attr('content');

    // Return the found image URL
    const imageUrl = ogImage;

    if (imageUrl) {
      return imageUrl;
    } else {
      return 'No rich preview image found';
    }

  } catch (error) {
    console.error('Error fetching the URL:', error.message);
    return 'Error fetching the URL';
  }
}

const Resources = () => {
    return (
        <>
        <Container>
            <h1>While you wait for us to deliver our app,
                here are some resources you may be interested in.
            </h1>
            <hr className="hr hr-blurry" />

            <h2>Job Info</h2>
            <div>
                <Card style={{ width: '18rem' }}>
                    <Card.Header>211 LA</Card.Header>
                    <Card.Body>
                        <Card.Img>
                            extractImageUrl(https://211la.org/)
                        </Card.Img>
                    </Card.Body>
                </Card>
            </div>
        </Container>
        </>)
}

export default Resources;
